const { Pool } = require('pg');

// Create a connection pool to the PostgreSQL database
const pool = new Pool({
  user: 'postgres.<username>', // Replace with your actual Supabase username
  host: '<host>', // Replace with your actual Supabase host
  database: 'postgres', // Replace with your actual Supabase database name
  password: '<pass>', // Replace with your actual Supabase password
  port: 5432, // Replace with your PostgreSQL port, typically 5432
});

async function runQuery(query) {
  try {
    // Execute the dynamic query
    const result = await pool.query(query);

    // Extract the rows from the result
    const queryResult = result.rows;

    // Return the result as a stringified JSON
    return JSON.stringify(queryResult);

  } catch (error) {
    // Handle any errors during the query execution and return error message as a string
    return JSON.stringify({ error: error.message });
  }
}

// Example call to the function with dynamic query
const dynamicQuery = query.sql_query;

// Call the function with the dynamic query
return runQuery(dynamicQuery);

