parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "article": {
      "type": "object",
      "required": ["category", "title", "metadata", "content", "hashtags"],
      "properties": {
        "category": {
          "type": "string",
          "description": "Article category"
        },
        "title": {
          "type": "string",
          "description": "Article title"
        },
        "metadata": {
          "type": "object",
          "properties": {
            "timePosted": {
              "type": "string",
              "description": "Time since article was posted"
            },
            "author": {
              "type": "string",
              "description": "Article author name"
            },
            "tag": {
              "type": "string",
              "description": "Article primary tag"
            }
          },
          "required": ["timePosted", "author", "tag"]
        },
        "content": {
          "type": "object",
          "properties": {
            "mainText": {
              "type": "string",
              "description": "Main article content"
            },
            "sections": {
              "type": "array",
              "items": {
                "type": "object",
                "properties": {
                  "title": {
                    "type": "string",
                    "description": "Section title"
                  },
                  "text": {
                    "type": "string",
                    "description": "Section content"
                  },
                  "quote": {
                    "type": "string",
                    "description": "Blockquote text"
                  }
                },
                "required": ["title", "text", "quote"]
              }
            }
          },
          "required": ["mainText", "sections"]
        },
        "hashtags": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Article hashtags"
        }
      }
    }
  }
}
